private void BacaTabelPesanan(){
        try{
            String sql="Select *From detailpemesanan order by id_pemesanan";
            kon.rs=kon.st.executeQuery(sql);
             ResultSetMetaData m=kon.rs.getMetaData();
            int kolom=m.getColumnCount();
            int baris=0;
            while(kon.rs.next()){
                baris=kon.rs.getRow();
            }
            datapemesan= new Object[baris][kolom];
            int x=0;
            kon.rs.beforeFirst();
            while(kon.rs.next()){
              datapemesan[x][0]=kon.rs.getString("id_pemesanan");
               datapemesan[x][1]=kon.rs.getString("kd_brg");
                datapemesan[x][2]=kon.rs.getString("nm_brg");
                datapemesan[x][3]=kon.rs.getString("deskripsi");
                datapemesan[x][4]=kon.rs.getString("nm_sp");
                datapemesan[x][5]=kon.rs.getString("alamat");
                datapemesan[x][6]=kon.rs.getString("harga_beli");
                datapemesan[x][7]=kon.rs.getString("jumlah");
                datapemesan[x][8]=kon.rs.getString("subtotal");
                x++;
}
            Tbl_Pesanan.setModel(new DefaultTableModel(datapemesan,labelpemesanan));
        }
        catch(SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }
    private void BacaTabelProdukCari()
    {
        try{
            String sql="Select *From detailpemesanan where id_pemesanan like '%" +tcari.getText()+"%'";
            kon.rs=kon.st.executeQuery(sql);
             ResultSetMetaData m=kon.rs.getMetaData();
            int kolom=m.getColumnCount();
            int baris=0;
            while(kon.rs.next()){
                baris=kon.rs.getRow();
            }
            datapemesan= new Object[baris][kolom];
            int x=0;
            kon.rs.beforeFirst();
            while(kon.rs.next()){
               datapemesan[x][0]=kon.rs.getString("id_pemesanan");
               datapemesan[x][1]=kon.rs.getString("kd_brg");
                datapemesan[x][2]=kon.rs.getString("nm_brg");
                datapemesan[x][3]=kon.rs.getString("deskripsi");
                datapemesan[x][4]=kon.rs.getString("nm_sp");
                datapemesan[x][5]=kon.rs.getString("alamat");
                datapemesan[x][6]=kon.rs.getString("harga_beli");
                datapemesan[x][7]=kon.rs.getString("jumlah");
                datapemesan[x][8]=kon.rs.getString("subtotal");
                x++;
}
            Tbl_Pesanan.setModel(new DefaultTableModel(datapemesan,labelpemesanan));
        }
        catch(SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }
