    private void Tbl_PesananMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Tbl_PesananMouseClicked
        // TODO add your handling code here:
      int tabelBarang = Tbl_Pesanan.getSelectedRow();
      masuk.IdPemesanan =Tbl_Pesanan.getValueAt(tabelBarang, 0).toString();
      masuk.KodeBarang =Tbl_Pesanan.getValueAt(tabelBarang, 1).toString();
      masuk.NamaBarang = Tbl_Pesanan.getValueAt(tabelBarang, 2).toString();
      
        masuk.NamaSupplier = Tbl_Pesanan.getValueAt(tabelBarang, 4).toString();
        masuk.Deskripsi = Tbl_Pesanan.getValueAt(tabelBarang, 3).toString();
        masuk.Jumlah = Tbl_Pesanan.getValueAt(tabelBarang, 7).toString();
        
        this.dispose();
    }//GEN-LAST:event_Tbl_PesananMouseClicked

