    private void jMenuItem12ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem12ActionPerformed
        // TODO add your handling code here:
            Process p = null;

        try {
            Runtime runtime = Runtime.getRuntime();
            p = runtime.exec("C:/xampp1/mysql/bin/mysqldump -uroot  --add-drop-database -B penjualan_db -r " +"D:/BackUp/" + "penjualan_db"+".sql");
            int processComplete = p.waitFor();
            if (processComplete == 0) {
                JOptionPane.showMessageDialog(null, "BackUp database Sukses Lokasi D:/BackUp/penjualan_db.sql");
            } 
            else  {
                JOptionPane.showMessageDialog(null, "BackUp database Gagal");
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jMenuItem12ActionPerformed

